mapper.update:
UPDATE MOIM_POST m
set m.MOIM_VIEWS = m.MOIM_VIEWS + 1
where m.MOIM_NUM = #{moimNum}

mapper.select:

SELECT *
FROM (
    SELECT MOIM_POST.*, ROWNUM rnum
    FROM (
         SELECT *
         FROM MOIM_POST


             AND CATEGORY_NUM = #{moimCategoryNum} 
             AND MOIM_TITLE LIKE '%'||#{keyword}||'%' OR MOIM_MAIN LIKE '%'||#{keyword}||'%'


             MOIM_NUM DESC 
             MOIM_VIEWS DESC 



         ) MOIM_POST
    WHERE ROWNUM <= #{cri.pageStart} + #{cri.perPageNum}
    )
WHERE rnum > #{cri.pageStart}

mapper.update:
UPDATE MOIM_POST m
set m.MOIM_MEMBER_COUNT = m.MOIM_MEMBER_COUNT + 1
where m.MOIM_NUM = #{moimNum}

UPDATE MOIM_POST m
set m.MOIM_PICTURES = #{path}
WHERE m.MOIM_NUM = #{moimNum}

mapper.select:

SELECT *
FROM (
    SELECT MOIM_POST.*, ROWNUM rnum
    FROM (
         SELECT *
         FROM MOIM_POST
         ORDER BY MOIM_NUM DESC
         ) MOIM_POST
    WHERE ROWNUM <= #{cri.pageStart} + #{cri.perPageNum}
    )
WHERE rnum > #{cri.pageStart}

SELECT MOIM_NUM
FROM MOIM_MEM
WHERE JOIN_NUM = #{joinNum}

SELECT count(*)
FROM MOIM_POST
WHERE USERID_NUM = #{userIdNum}

mapper.update:
UPDATE PROFILE p
set p.HOSTING_COUNT = P.HOSTING_COUNT + 1
where p.USERID_NUM = #{userIdNum}

mapper.select:
SELECT USER_NAME
FROM USERS
WHERE USERID_NUM = #{moimHostUserIdNum}
